// src/components/StudentList.jsx
import React, { useEffect, useState, useMemo, useCallback } from 'react'
import api from '../services/api'
import { useNavigate } from 'react-router-dom'
import { useTable, useGlobalFilter, useSortBy } from 'react-table'
import { motion } from 'framer-motion'
import { Edit2, Trash2 } from 'lucide-react'
import { toast } from 'react-toastify'

export default function StudentList() {
  const [students, setStudents] = useState([])
  const [globalFilter, setGlobalFilter] = useState('')
  const navigate = useNavigate()

  // Carga datos
  useEffect(() => {
    api.get('/estudiantes/')
      .then(res => Array.isArray(res.data) ? setStudents(res.data) : setStudents([]))
      .catch(() => {
        toast.error('Error al cargar estudiantes')
        setStudents([])
      })
  }, [])

  // Elimina estudiante
  const deleteOne = useCallback(async (id) => {
    if (!window.confirm('Â¿Eliminar este estudiante?')) return
    await api.delete(`/estudiantes/${id}/`)
    toast.success('Estudiante eliminado')
    const res = await api.get('/estudiantes/')
    setStudents(Array.isArray(res.data) ? res.data : [])
  }, [])

  // Asegurarnos de que data sea siempre un array
  const data = useMemo(() => Array.isArray(students) ? students : [], [students])

  const columns = useMemo(() => [
    { Header: 'Nombre', accessor: 'nombre' },
    { Header: 'Carrera', accessor: 'carrera' },
    { Header: 'Email', accessor: 'email' },
    {
      Header: 'Foto',
      accessor: 'foto',
      Cell: ({ cell: { value }, row: { id } }) => (
        <img
          key={`foto-${id}`}
          src={value}
          alt=""
          className="w-10 h-10 rounded-full object-cover"
        />
      )
    },
    {
      Header: 'Acciones',
      id: 'acciones',
      Cell: ({ row }) => (
        <div key={`actions-${row.original.id}`} className="flex space-x-2">
          <button
            onClick={() => navigate(`/students/edit/${row.original.id}`)}
            className="hover:text-yellow-600"
          >
            <Edit2 size={18} />
          </button>
          <button
            onClick={() => deleteOne(row.original.id)}
            className="hover:text-red-600"
          >
            <Trash2 size={18} />
          </button>
        </div>
      )
    }
  ], [navigate, deleteOne])

  const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    rows,
    prepareRow
  } = useTable(
    { columns, data, initialState: { globalFilter } },
    useGlobalFilter,
    useSortBy
  )

  return (
    <motion.div
      className="p-4 bg-white rounded-lg shadow"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
    >
      <div className="flex justify-between items-center mb-4">
        <h2 className="text-xl font-semibold">Estudiantes</h2>
        <button
          onClick={() => navigate('/students/new')}
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          Nuevo
        </button>
      </div>

      <input
        type="text"
        placeholder="Buscar..."
        value={globalFilter}
        onChange={e => setGlobalFilter(e.target.value)}
        className="mb-4 p-2 border rounded w-full"
      />

      <table {...getTableProps()} className="w-full table-auto">
        <thead className="bg-gray-100">
          {headerGroups.map(hg => (
            <tr {...hg.getHeaderGroupProps()} key={hg.id}>
              {hg.headers.map(col => (
                <th
                  {...col.getHeaderProps(col.getSortByToggleProps())}
                  key={col.id}
                  className="p-2 text-left cursor-pointer"
                >
                  {col.render('Header')}
                  {col.isSorted
                    ? col.isSortedDesc
                      ? ' ğŸ”½'
                      : ' ğŸ”¼'
                    : ''}
                </th>
              ))}
            </tr>
          ))}
        </thead>
        <tbody {...getTableBodyProps()}>
          {rows.map(row => {
            prepareRow(row)
            return (
              <tr {...row.getRowProps()} key={row.original.id} className="hover:bg-gray-50">
                {row.cells.map(cell => (
                  <td {...cell.getCellProps()} key={`${row.original.id}-${cell.column.id}`} className="p-2">
                    {cell.render('Cell')}
                  </td>
                ))}
              </tr>
            )
          })}
        </tbody>
      </table>
    </motion.div>
  )
}
