import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import api from '../services/api';

function StudentList() {
  const [students, setStudents] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetchStudents();
  }, []);

  const fetchStudents = async () => {
    const res = await api.get('estudiantes/');
    setStudents(res.data);
  };

  const handleDelete = async (id) => {
    if (window.confirm('¿Eliminar estudiante?')) {
      await api.delete(`estudiantes/${id}/`);
      fetchStudents();
    }
  };

  return (
    <div>
      <h2>Estudiantes</h2>
      <button onClick={() => navigate('/estudiantes/new')}>Nuevo Estudiante</button>
      <table>
        <thead>
          <tr>
            <th>Nombre</th>
            <th>Carrera</th>
            <th>Email</th>
            <th>Foto</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {students.map(s => (
            <tr key={s.id}>
              <td>{s.nombre}</td>
              <td>{s.carrera}</td>
              <td>{s.email}</td>
              <td>
                {s.foto && <img src={s.foto} alt="" width="50" />}
              </td>
              <td>
                <button onClick={() => navigate(`/estudiantes/edit/${s.id}`)}>Editar</button>
                <button onClick={() => handleDelete(s.id)}>Eliminar</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default StudentList;
