import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import api from '../services/api';

function StudentForm() {
  const [form, setForm] = useState({ nombre: '', carrera: '', email: '', foto: null });
  const navigate = useNavigate();
  const { id } = useParams();

  useEffect(() => {
    if (id) loadStudent();
  }, [id]);

  const loadStudent = async () => {
    const res = await api.get(`estudiantes/${id}/`);
    setForm({ nombre: res.data.nombre, carrera: res.data.carrera, email: res.data.email, foto: null });
  };

  const handleChange = e => {
    const { name, value, files } = e.target;
    setForm(prev => ({ ...prev, [name]: files ? files[0] : value }));
  };

  const handleSubmit = async e => {
    e.preventDefault();
    const data = new FormData();
    data.append('nombre', form.nombre);
    data.append('carrera', form.carrera);
    data.append('email', form.email);
    if (form.foto) data.append('foto', form.foto);

    if (id) {
      await api.patch(`estudiantes/${id}/`, data);
    } else {
      await api.post('estudiantes/', data);
    }
    navigate('/');
  };

  return (
    <div>
      <h2>{id ? 'Editar Estudiante' : 'Nuevo Estudiante'}</h2>
      <form onSubmit={handleSubmit} encType="multipart/form-data">
        <div>
          <label>Nombre:</label>
          <input type="text" name="nombre" value={form.nombre} onChange={handleChange} required />
        </div>
        <div>
          <label>Carrera:</label>
          <input type="text" name="carrera" value={form.carrera} onChange={handleChange} required />
        </div>
        <div>
          <label>Email:</label>
          <input type="email" name="email" value={form.email} onChange={handleChange} required />
        </div>
        <div>
          <label>Foto:</label>
          <input type="file" name="foto" onChange={handleChange} accept="image/*" />
        </div>
        <button type="submit">Guardar</button>
      </form>
    </div>
  );
}

export default StudentForm;
