import { useEffect, useState } from 'react'
import api from '../services/api'
import { BarChart, Bar, XAxis, YAxis, Tooltip, ResponsiveContainer } from 'recharts'
import { motion } from 'framer-motion'

export default function StudentChart() {
  const [data, setData] = useState([])

  useEffect(() => {
    const load = async () => {
      const est = await axios.get('/api/estudiantes/')
      const mat = await axios.get('/api/matriculas/')
      // contar cantidad de matrículas por estudiante
      const counts = est.data.map(s => ({
        nombre: s.nombre,
        matriculas: mat.data.filter(m => m.estudiante === s.id).length
      }))
      setData(counts)
    }
    load()
  }, [])

  return (
    <motion.div 
      className="p-4 bg-white rounded-lg shadow"
      initial={{ scale: 0.8, opacity: 0 }}
      animate={{ scale: 1, opacity: 1 }}
    >
      <h2 className="text-xl font-semibold mb-4">Matrículas por Estudiante</h2>
      <ResponsiveContainer width="100%" height={300}>
        <BarChart data={data}>
          <XAxis dataKey="nombre" />
          <YAxis />
          <Tooltip />
          <Bar dataKey="matriculas" name="Matrículas" />
        </BarChart>
      </ResponsiveContainer>
    </motion.div>
  )
}
