import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import api from '../services/api';

function EnrollmentForm() {
  const [form, setForm] = useState({ estudiante: '', curso: '', semestre: '', fecha: '', comprobante: null });
  const navigate = useNavigate();
  const { id } = useParams();

  useEffect(() => {
    if (id) loadEnrollment();
    // Cargar lista de estudiantes si quieres select
  }, [id]);

  const loadEnrollment = async () => {
    const res = await api.get(`matriculas/${id}/`);
    setForm({ estudiante: res.data.estudiante, curso: res.data.curso, semestre: res.data.semestre, fecha: res.data.fecha, comprobante: null });
  };

  const handleChange = e => {
    const { name, value, files } = e.target;
    setForm(prev => ({ ...prev, [name]: files ? files[0] : value }));
  };

  const handleSubmit = async e => {
    e.preventDefault();
    const data = new FormData();
    data.append('estudiante', form.estudiante);
    data.append('curso', form.curso);
    data.append('semestre', form.semestre);
    data.append('fecha', form.fecha);
    if (form.comprobante) data.append('comprobante', form.comprobante);

    if (id) {
      await api.patch(`matriculas/${id}/`, data);
    } else {
      await api.post('matriculas/', data);
    }
    navigate('/matriculas');
  };

  return (
    <div>
      <h2>{id ? 'Editar Matrícula' : 'Nueva Matrícula'}</h2>
      <form onSubmit={handleSubmit} encType="multipart/form-data">
        <div>
          <label>Estudiante (ID):</label>
          <input type="text" name="estudiante" value={form.estudiante} onChange={handleChange} required />
        </div>
        <div>
          <label>Curso:</label>
          <input type="text" name="curso" value={form.curso} onChange={handleChange} required />
        </div>
        <div>
          <label>Semestre:</label>
          <input type="text" name="semestre" value={form.semestre} onChange={handleChange} required />
        </div>
        <div>
          <label>Fecha:</label>
          <input type="date" name="fecha" value={form.fecha} onChange={handleChange} required />
        </div>
        <div>
          <label>Comprobante:</label>
          <input type="file" name="comprobante" onChange={handleChange} accept="image/*" />
        </div>
        <button type="submit">Guardar</button>
      </form>
    </div>
  );
}

export default EnrollmentForm;
