// src/components/StudentList.jsx
import React, { useEffect, useState, useMemo, useCallback } from 'react'
import axios from 'axios'
import { useNavigate } from 'react-router-dom'
import { useTable, useGlobalFilter, useSortBy } from 'react-table'
import { motion } from 'framer-motion'
import { Edit2, Trash2 } from 'lucide-react'
import { toast } from 'react-toastify'

export default function StudentList() {
  const [students, setStudents] = useState([])
  const [globalFilter, setGlobalFilter] = useState('')
  const navigate = useNavigate()

  // Traer todos
  useEffect(() => {
    axios.get('/api/estudiantes/')
      .then(res => setStudents(res.data))
      .catch(() => toast.error('Error al cargar estudiantes'))
  }, [])

  // Eliminar uno (memoizado)
  const deleteOne = useCallback(async (id) => {
    if (!window.confirm('Â¿Eliminar este estudiante?')) return
    await axios.delete(`/api/estudiantes/${id}/`)
    toast.success('Estudiante eliminado')
    // refresca lista
    const res = await axios.get('/api/estudiantes/')
    setStudents(res.data)
  }, [])

  // Configurar tabla
  const data = useMemo(() => students, [students])
  const columns = useMemo(() => [
    { Header: 'Nombre', accessor: 'nombre' },
    { Header: 'Carrera', accessor: 'carrera' },
    { Header: 'Email', accessor: 'email' },
    {
      Header: 'Foto',
      accessor: 'foto',
      Cell: ({ cell: { value } }) => (
        <img src={value} alt="" className="w-10 h-10 rounded-full object-cover" />
      )
    },
    {
      Header: 'Acciones',
      id: 'actions',
      Cell: ({ row }) => (
        <div className="flex space-x-2">
          <button
            onClick={() => navigate(`/students/edit/${row.original.id}`)}
            className="hover:text-yellow-600"
          >
            <Edit2 size={18} />
          </button>
          <button
            onClick={() => deleteOne(row.original.id)}
            className="hover:text-red-600"
          >
            <Trash2 size={18} />
          </button>
        </div>
      )
    }
  ], [navigate, deleteOne])

  const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    rows,
    prepareRow
  } = useTable(
    {
      columns,
      data,
      initialState: { globalFilter },  // <-- filtrado controlado
    },
    useGlobalFilter,
    useSortBy
  )

  return (
    <motion.div
      className="p-4 bg-white rounded-lg shadow"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
    >
      <div className="flex justify-between items-center mb-4">
        <h2 className="text-xl font-semibold">Estudiantes</h2>
        <button
          onClick={() => navigate('/students/new')}
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          Nuevo
        </button>
      </div>

      <input
        type="text"
        placeholder="Buscar..."
        value={globalFilter}
        onChange={e => setGlobalFilter(e.target.value)}
        className="mb-4 p-2 border rounded w-full"
      />

      <table {...getTableProps()} className="w-full table-auto">
        <thead className="bg-gray-100">
          {headerGroups.map(headerGroup => (
            <tr {...headerGroup.getHeaderGroupProps()} key={headerGroup.id}>
              {headerGroup.headers.map(column => (
                <th
                  {...column.getHeaderProps(column.getSortByToggleProps())}
                  key={column.id}
                  className="p-2 text-left cursor-pointer"
                >
                  {column.render('Header')}
                  {column.isSorted
                    ? column.isSortedDesc
                      ? ' ðŸ”½'
                      : ' ðŸ”¼'
                    : ''}
                </th>
              ))}
            </tr>
          ))}
        </thead>
        <tbody {...getTableBodyProps()}>
          {rows.map(row => {
            prepareRow(row)
            return (
              <tr {...row.getRowProps()} key={row.id} className="hover:bg-gray-50">
                {row.cells.map(cell => (
                  <td {...cell.getCellProps()} key={cell.column.id} className="p-2">
                    {cell.render('Cell')}
                  </td>
                ))}
              </tr>
            )
          })}
        </tbody>
      </table>
    </motion.div>
  )
}
