import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import api from '../services/api';

function EnrollmentList() {
  const [enrollments, setEnrollments] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetchEnrollments();
  }, []);

  const fetchEnrollments = async () => {
    const res = await api.get('matriculas/');
    setEnrollments(res.data);
  };

  const handleDelete = async (id) => {
    if (window.confirm('¿Eliminar matrícula?')) {
      await api.delete(`matriculas/${id}/`);
      fetchEnrollments();
    }
  };

  return (
    <div>
      <h2>Matrículas</h2>
      <button onClick={() => navigate('/matriculas/new')}>Nueva Matrícula</button>
      <table>
        <thead>
          <tr>
            <th>Estudiante</th>
            <th>Curso</th>
            <th>Semestre</th>
            <th>Fecha</th>
            <th>Comprobante</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {enrollments.map(m => (
            <tr key={m.id}>
              <td>{m.estudiante_nombre || m.estudiante}</td>
              <td>{m.curso}</td>
              <td>{m.semestre}</td>
              <td>{new Date(m.fecha).toLocaleDateString()}</td>
              <td>
                {m.comprobante && <img src={m.comprobante} alt="" width="50" />}
              </td>
              <td>
                <button onClick={() => navigate(`/matriculas/edit/${m.id}`)}>Editar</button>
                <button onClick={() => handleDelete(m.id)}>Eliminar</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default EnrollmentList;
