// src/services/api.js
import axios from 'axios'

const api = axios.create({
  baseURL: 'http://127.0.0.1:8000/api',  // apunta directo al backend
  headers: {
    'Content-Type': 'application/json',
  },
  // no enviamos cookies/credenciales, así Django no intentará CSRF
  withCredentials: false,
})

export default api
