import { useEffect, useState, useMemo } from 'react'
import axios from 'axios'
import { useNavigate } from 'react-router-dom'
import { useTable, useGlobalFilter, useSortBy } from 'react-table'
import { motion } from 'framer-motion'
import { Edit2, Trash2 } from 'lucide-react'
import { toast } from 'react-toastify'

export default function StudentList() {
  const [students, setStudents] = useState([])
  const [globalFilter, setGlobalFilter] = useState('')
  const navigate = useNavigate()

  useEffect(() => {
    fetch()
  }, [])

  const fetch = async () => {
    const res = await axios.get('/api/estudiantes/')
    setStudents(res.data)
  }

  const deleteOne = async (id) => {
    if (!confirm('Â¿Eliminar este estudiante?')) return
    await axios.delete(`/api/estudiantes/${id}/`)
    toast.success('Estudiante eliminado')
    fetch()
  }

  const data = useMemo(() => students, [students])
  const columns = useMemo(() => [
    { header: 'Nombre', accessorKey: 'nombre' },
    { header: 'Carrera', accessorKey: 'carrera' },
    { header: 'Email', accessorKey: 'email' },
    {
      header: 'Foto',
      accessorFn: row => (
        <img src={row.foto} alt="" className="w-10 h-10 rounded-full object-cover" />
      )
    },
    {
      header: 'Acciones',
      accessorFn: row => (
        <div className="flex space-x-2">
          <button onClick={() => navigate(`/students/edit/${row.id}`)} className="hover:text-yellow-600">
            <Edit2 size={18} />
          </button>
          <button onClick={() => deleteOne(row.id)} className="hover:text-red-600">
            <Trash2 size={18} />
          </button>
        </div>
      )
    }
  ], [navigate])

  const table = useTable({ data, columns }, useGlobalFilter, useSortBy)

  return (
    <motion.div 
      className="p-4 bg-white rounded-lg shadow"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
    >
      <div className="flex justify-between items-center mb-4">
        <h2 className="text-xl font-semibold">Estudiantes</h2>
        <button onClick={() => navigate('/students/new')} className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700">
          Nuevo
        </button>
      </div>
      <input
        type="text"
        placeholder="Buscar..."
        value={globalFilter}
        onChange={e => setGlobalFilter(e.target.value)}
        className="mb-4 p-2 border rounded w-full"
      />
      <table className="w-full table-auto">
        <thead className="bg-gray-100">
          {table.getHeaderGroups().map(headerGroup => (
            <tr key={headerGroup.id}>
              {headerGroup.headers.map(header => (
                <th
                  key={header.id}
                  className="p-2 text-left cursor-pointer"
                  onClick={header.column.getToggleSortingHandler()}
                >
                  {header.column.columnDef.header}
                  {header.column.getIsSorted() === 'asc' ? ' ðŸ”¼' : header.column.getIsSorted() === 'desc' ? ' ðŸ”½' : ''}
                </th>
              ))}
            </tr>
          ))}
        </thead>
        <tbody>
          {table.getRowModel().rows.map(row => (
            <tr key={row.id} className="hover:bg-gray-50">
              {row.getVisibleCells().map(cell => (
                <td key={cell.id} className="p-2">
                  {cell.renderCell()}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </motion.div>
  )
}
